import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './pages/Home';
import About from './pages/About';
import Contact from './pages/Contact';
import JobList from './components/JobList';
import JobCard from './components/JobCard';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';
import PostJobForm from './components/PostJobForm';
import ApplyForm from './components/ApplyForm';
import AdminPanel from './components/AdminPanel';
import './App.css';
import { createContext, useState, useEffect } from 'react';

export const AuthContext = createContext();

function PrivateRoute({ children }) {
  const { token } = React.useContext(AuthContext);
  if (!token) {
    window.location.href = '/login';
    return null;
  }
  return children;
}

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [user, setUser] = useState(null);

  useEffect(() => {
    if (token) {
      setUser({}); // Optionally decode token for user info
    } else {
      setUser(null);
    }
  }, [token]);

  const login = (tok) => {
    setToken(tok);
    localStorage.setItem('token', tok);
  };
  const logout = () => {
    setToken(null);
    localStorage.removeItem('token');
  };

  return (
    <AuthContext.Provider value={{ token, user, login, logout }}>
      <Router>
        <Navbar />
        <div className="container">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/jobs" element={<PrivateRoute><JobList /></PrivateRoute>} />
            <Route path="/login" element={<LoginForm />} />
            <Route path="/register" element={<RegisterForm />} />
            <Route path="/post-job" element={<PrivateRoute><PostJobForm /></PrivateRoute>} />
            <Route path="/apply/:jobId" element={<PrivateRoute><ApplyForm /></PrivateRoute>} />
            <Route path="/admin" element={<PrivateRoute><AdminPanel /></PrivateRoute>} />
          </Routes>
        </div>
        <Footer />
      </Router>
    </AuthContext.Provider>
  );
}

export default App;
